sp_widget.client_script:
function($scope,spUtil,glideUserSession,$sce) {
	/* widget controller */
	var c = this;
	$scope.runtime_url = $sce.trustAsResourceUrl( c.data.runtime );

	glideUserSession.loadCurrentUser().then(function(currentUser) {
		spUtil.recordWatch($scope, 'new_call', "u_twilio_active=true^u_twilio_assigned_to="+currentUser.userID, function(name,data) {
			spUtil.update($scope);
		});
	});
}

