sp_widget.script:
(function() {
	data.calls =[];
	data.runtime = "https://flex.twilio.com/"+gs.getProperty('twilio.flex.runtime');
	var sdagent = gs.getUserID();
	var calls_gr = new GlideRecord('new_call');
	calls_gr.addQuery('u_twilio_active', true);
	calls_gr.addQuery('u_twilio_assigned_to', sdagent);
	calls_gr.addQuery('u_twilio_int_informationNOT LIKEcall_completed');

	calls_gr.orderBy('number');
	calls_gr.query();

	while(calls_gr.next())
		{

		var call ={};
		call.sys_id = calls_gr.getValue('sys_id');
		call.number = calls_gr.getValue('number');
		call.caller = calls_gr.caller.getDisplayValue();
	call.url ='/new_call.do?sys_id='+calls_gr.sys_id;


			call.added_class ='';
			if(data.calls.length==0)
				{
				call.added_class = 'active';
			}
			data.calls.push(call);

		}


	})();
